INSERT INTO `artikel` (`id`, `judul`, `isi`, `konten`, `gambar`, `created_at`, `updated_at`, `penulis`, `kategori`, `tags`) VALUES
(18, 'Menelusuri Jejak Sejarah di Kampung Amsterdam Dayeuhmanggung', 'Jika Anda menyukai wisata sejarah dengan nuansa arsitektur Eropa klasik, maka Kampung Amsterdam di Dayeuhmanggung adalah tempat yang wajib dikunjungi. Terletak di tengah kebun teh, kampung ini menghadirkan deretan rumah bergaya kolonial yang menjadi saksi bisu masa penjajahan Belanda di Garut.\r\n\r\n🔹 Lokasi & Akses\r\nKampung Amsterdam berada dalam kawasan wisata Dayeuhmanggung, sekitar 10 menit berjalan kaki dari pintu masuk utama. Jalan menuju lokasi dapat ditempuh dengan berjalan kaki menyusuri jalan setapak di antara kebun teh, cocok untuk Anda yang ingin menikmati perjalanan santai sambil menikmati udara sejuk.\r\n\r\n🔹 Daya Tarik Utama\r\nKampung ini memiliki sekitar 12 rumah peninggalan Belanda dengan gaya bangunan Eropa tahun 1930-an yang masih berdiri kokoh. Selain rumah-rumah tua, terdapat taman kecil, saluran air kuno, balai pohon, hingga properti unik seperti perahu kayu yang disediakan sebagai spot foto. Suasananya tenang dan klasik, cocok untuk wisata edukatif maupun fotografi.\r\n\r\n🔹 Tips Berkunjung\r\nDatang pada siang hari agar pencahayaan optimal untuk fotografi.\r\n\r\nGunakan alas kaki yang nyaman karena jalan cukup menanjak.\r\n\r\nHormati kawasan dan tidak merusak properti atau spot foto.\r\n\r\nBawa air minum sendiri karena tidak ada warung di dalam kampung.', '', '1753069871044_camp.jpg', '2025-07-21 03:09:13', '2025-07-21 03:51:11', 'Admin Dayeuhmanggung', 'Wisata Sejarah', 'KampungAmsterdam,GarutHeritage,DayeuhmanggungRumahBelanda,WisataEdukasi'),
(19, 'Menikmati Kabut Pagi di Kebun Teh Dayeuhmanggung', 'Kebun Teh Dayeuhmanggung adalah destinasi wisata alam yang menawarkan suasana sejuk, hamparan hijau, dan ketenangan khas pegunungan. Setiap pagi, kawasan ini diselimuti kabut tipis yang menciptakan suasana magis dan menenangkan, menjadikannya tempat yang sempurna untuk melepas penat dari hiruk pikuk kota.\r\n\r\n🔹 Lokasi & Akses\r\nKebun Teh Dayeuhmanggung terletak di Desa Sukatani, Kecamatan Cilawu, Kabupaten Garut, Jawa Barat. Lokasinya berada di lereng Gunung Cikuray pada ketinggian sekitar 1.300 mdpl. Untuk mencapai tempat ini, pengunjung dapat menempuh perjalanan ±45 menit dari pusat kota Garut melalui jalur Cilawu. Akses jalan sudah cukup baik dan dapat dilalui kendaraan roda dua maupun roda empat.\r\n\r\n🔹 Daya Tarik Utama\r\nDaya tarik utama dari kebun teh ini adalah pemandangan alam yang luas dan tertata rapi, udara segar pegunungan, serta kabut pagi yang memukau. Selain itu, pengunjung dapat berjalan santai di tengah kebun, berfoto di spot ikonik seperti Jembatan Teh, hingga menyaksikan langsung aktivitas petani memetik daun teh. Suasana alami dan damai menjadikan tempat ini cocok untuk relaksasi maupun kegiatan fotografi alam.\r\n\r\n🔹 Tips Berkunjung\r\nDatanglah pagi hari sebelum pukul 08.00 untuk menikmati kabut dan cahaya matahari yang lembut.\r\n\r\nGunakan alas kaki yang nyaman karena akan banyak berjalan di jalur tanah dan batu.\r\n\r\nBawa bekal makanan jika ingin bersantai lebih lama, karena warung hanya tersedia di pintu masuk.\r\n\r\nSiapkan kamera atau ponsel dengan baterai penuh untuk mengabadikan momen kabut pagi yang indah.', '', '1753069776997_camp.jpg', '2025-07-21 03:49:37', '2025-07-21 03:49:37', 'Admin', 'Wisata', 'alam,dayeuhmanggung,wisata'),
(20, 'Curug Abeh: Air Terjun Tersembunyi di Hutan Dayeuhmanggung', '🔹 Paragraf Pembuka\r\nDi balik rimbunnya hutan pinus dan perkebunan teh Dayeuhmanggung, tersembunyi sebuah air terjun alami bernama Curug Abeh. Tempat ini menawarkan suasana tenang dan alami yang cocok bagi Anda yang mencari pelarian dari hiruk-pikuk kehidupan kota.\r\n\r\n🔹 Lokasi & Akses\r\nCurug Abeh terletak di Desa Sukatani, dan dapat dicapai dengan trekking selama ±30 menit dari Kampung Amsterdam. Rute perjalanan melewati kebun teh, hutan pinus, dan jalur tanah alami. Meski jalannya sedikit menantang, pemandangan sepanjang perjalanan sangat memuaskan.\r\n\r\n🔹 Daya Tarik Utama\r\nAir terjun ini memiliki tinggi sekitar 10 meter dengan aliran jernih dan kolam dangkal di bawahnya, cocok untuk bermain air atau sekadar merendam kaki. Suasana sekitarnya masih alami, sunyi, dan sejuk—menjadikan Curug Abeh sebagai spot healing yang ideal. Karena belum banyak dijamah wisatawan, tempat ini tetap terjaga kebersihannya dan memberikan sensasi petualangan tersendiri.\r\n\r\n🔹 Tips Berkunjung\r\nPakai sepatu atau sandal gunung karena jalur bisa licin saat basah\r\n\r\nBawa air minum dan camilan secukupnya karena tidak ada warung di area curug\r\n\r\nJangan lupa bawa kantong sampah sendiri untuk menjaga kebersihan\r\n\r\nDisarankan datang bersama teman untuk alasan keamanan', '', '1754447089509_WhatsApp Image 2025-08-03 at 11.03.35_aaf73e36.jpg', '2025-07-21 03:54:05', '2025-08-06 02:24:49', 'Adminn', 'Wisata Alam', 'CurugAbeh,AirTerjunGarut,HiddenGemTrekkingDayeuhmanggung,WisataAlamSunyi');

